import React from "react";
import "./style.css";

const PopularGames = ({ popularGames }) => {
  return (
    <div>
      <center><h1>Popular Games</h1></center>
      <div className="card" style={{ width: "18rem" }}>
        <img src="{popularGames.thumb_url}" className="card-img-top" alt="{popularGames.name}" />
        <div className="card-body">
          <h5 className="card-title">{popularGames.name}</h5>
          <p className="card-text">{popularGames.description}</p>
          <a href="{popularGames.url}" className="btn btn-warning">Link</a>
        </div>
      </div>
    </div>
  )
};

export default PopularGames;
