import axios from "axios";
require("dotenv").config();

const KEY = process.env.REACT_APP_BGA_API_KEY;

export default {
  // Gets all Games
  getGames: function() {
    return axios.get("/api/games");
  },
  // finds all Games with searched title from bga Games
  searchGames: function (title) {
    return axios.get("https://www.boardgameatlas.com/api/search?name=" + title + "&client_id=" + KEY);
  },
  // Saves a Game to the database
  saveGame: function(gameData) {
    return axios.post("/api/games", gameData);
  },
  // Deletes the Game with the given id
  deleteGame: function(id) {
    return axios.delete("/api/games/" + id);
  }
};
