import React from "react";
import API from "../utils/API";
import "./Home.css";


class Home extends React.Component {
  state = {
    popularGames: []
  }

  componentDidMount() {
    API.getPopular()
      .then(
        (response) => {
          console.log(response)
          this.setState({ popularGames: response.data })
      }
    )
  }


  render() {
    return (
      <main>
        <center><h1>Popular Games</h1></center>
        <div className="row">
          {this.state.popularGames.map(game => (
            <div className="card" id={game.id} key={game.id} style={{ width: "18rem" }}>
            <img src={game.images.original} className="card-img-top" alt={game.name} />
            <div className="card-body">
              <h5 className="card-title">{game.name}</h5>
              <p className="card-text">{game.description_preview}</p>
              <a href="{game.url}" className="btn btn-warning">More Info</a>
            </div>
            </div>
          ))}
          </div>
      </main>
    )
  }

}

export default Home;
