import React from "react";
import "./style.css";


class PopularGames extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      saved: false,
      deleted: false
    }
  }

  render() {
    return (
      <div>
        <center><h1>Popular Games</h1></center>
        <div className="card" style={{ width: "18rem" }}>
          <img src="{this.props.thumb_url}" className="card-img-top" alt="{this.props.name}" />
          <div className="card-body">
            <h5 className="card-title">{this.props.name}</h5>
            <p className="card-text">{this.props.description}</p>
            <a href="{this.props.url}" className="btn btn-warning">Link</a>
          </div>
        </div>
      </div>
    )
  }
}
export default PopularGames;
